CREATE DEFINER=`root`@`localhost` PROCEDURE `catalog_search`(IN `inSearchString` TEXT, IN `inAllWords` VARCHAR(3), IN `inShortProductDescriptionLength` INT, IN `inProductPerPage` INT, IN `inStartItem` INT)
    NO SQL
BEGIN
IF inAllWords = "on"  THEN
